// Process AI Agent analysis results
const analysis = $json;

console.log('ðŸ” Processing AI analysis results:', {
  agenda_items: analysis.agenda_items?.length || 0,
  privacy_issues: analysis.privacy_issues?.length || 0,
  action_items: analysis.action_items?.length || 0
});

// Prepare agenda updates
const agendaUpdates = analysis.agenda_items
  .filter(item => item.status === 'done')
  .map(item => ({
    agenda_id: item.agenda_item_id,
    status: 'completed'
  }));

// Generate report content
const reportContent = `# Meeting Analysis Report

## Agenda Items
${analysis.agenda_items.map(item => `
### ${item.title}
- Status: ${item.status}
- ${item.description}
`).join('')}

## Privacy Issues (${analysis.privacy_issues.length} found)
${analysis.privacy_issues.map((issue, index) => `
${index + 1}. **${issue.type}** (${issue.severity})
   - ${issue.description}
   - Recommendation: ${issue.recommendation}
`).join('')}

## Action Items (${analysis.action_items.length} found)
${analysis.action_items.map((action, index) => `
${index + 1}. ${action.description}
   - Assigned to: ${action.assigned_to}
   - Deadline: ${action.deadline || 'Not specified'}
   - Priority: ${action.priority}
`).join('')}

## Mentioned Persons
${analysis.mentioned_persons.map(person => `
- ${person.name} ${person.is_participant ? '(Participant)' : '(External)'}
  - Context: ${person.context}
`).join('')}
`;

return {
  analysis: analysis,
  agenda_updates: agendaUpdates,
  report: {
    meeting_id: analysis.meeting_info.meeting_id,
    title: `Meeting Analysis - ${analysis.meeting_info.title}`,
    content: reportContent,
    metadata: {
      privacy_issues_count: analysis.privacy_issues.length,
      action_items_count: analysis.action_items.length,
      agenda_items_discussed: analysis.agenda_items.filter(i => i.status === 'done').length
    }
  }
};
